import {ChangeDetectionStrategy, Component} from '@angular/core';
import {Page} from './models/page';
import {Router} from '@angular/router';

@Component({
  selector: 'lhl-feature-header',
  templateUrl: './feature-header.component.html',
  styleUrls: ['./feature-header.component.scss'],
  changeDetection: ChangeDetectionStrategy.OnPush
})
export class FeatureHeaderComponent {
  logoImg = 'assets/logo.png'
  currentPageID = '1';

  pages: Array<Page> = [
    {id: '1', name: 'Home', url: ''}
  ]

  constructor(private router: Router) {}

  get abbrName(): string {
    return 'OK';
  }

  async onClickPage(page: Page) {
    this.currentPageID = page.id;
    await this.router.navigate([page.url]);
  }

  linkStyle(id: string): string {
    return this.currentPageID === id ? 'link-current' : '';
  }
}
<header class="site-header">
  <div class="header-left">
    <img [src]="logoImg" class="logo" alt="logo" />
    <div class="header-pages">
      <a class="link" *ngFor="let page of pages" [ngClass]="linkStyle(page.id)" (click)="onClickPage(page)">{{page.name}}</a>
    </div>
  </div>
  <div class="header-right">
    <div class="abbreviated-name">
      <span>{{abbrName}}</span>
    </div>
  </div>
</header>
